pragma solidity ^0.8.0;

import './crowdsale/IncreasingInterestCrowdsale.sol';
import './base/LongSaleInterest.sol';

contract MintedIncreasingInterestTGE is IncreasingInterestCrowdsale, LongSaleInterest {
    using ConfigHelper for Registry;

    bool public longSale;
    uint256 public timeStartEarningInterest;
    uint256 public termLengthInSeconds;
    uint256 public interestRate;
    uint256 public yield;

    function initialize(
        Registry _registry,
        address _pool,
        address _token,
        address _currency,
        bool _longSale
    ) public initializer {
        __Crowdsale__init();

        registry = _registry;
        pool = _pool;
        token = _token;
        currency = _currency;
        longSale = _longSale;
    }

    function isLongSale() public view override returns (bool) {
        return longSale;
    }

    function setYield(uint256 _yield) public whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        yield = _yield;
    }

    function setupLongSale(
        uint256 _interestRate,
        uint256 _termLengthInSeconds,
        uint256 _timeStartEarningInterest
    ) public whenNotPaused nonReentrant securitizationPoolRestricted {
        if (isLongSale()) {
            interestRate = _interestRate;
            timeStartEarningInterest = _timeStartEarningInterest;
            termLengthInSeconds = _termLengthInSeconds;
            yield = _interestRate;
        }
    }

    function getLongSaleTokenPrice(uint256 timestamp) public view returns (uint256) {
        if (!finalized) return (RATE_SCALING_FACTOR**2) / rate;
        else if (
            Configuration.NOTE_TOKEN_TYPE(INoteToken(token).noteTokenType()) == Configuration.NOTE_TOKEN_TYPE.JUNIOR
        ) {
            address sotTgeAddress = ISecuritizationPool(pool).tgeAddress();
            if (sotTgeAddress != address(0) && !FinalizableCrowdsale(sotTgeAddress).finalized())
                return (RATE_SCALING_FACTOR**2) / rate;
            return registry.getDistributionAssessor().calcTokenPrice(pool, token);
        } else {
            require(
                getTimeStartEarningInterest() != 0,
                'MintedIncreasingInterestTGE: timeStartEarningInterest need to be setup'
            );
            return getPurchasePrice(interestRate, yield, timestamp - timeStartEarningInterest, termLengthInSeconds);
        }
    }

    function getLongSaleTokenAmount(uint256 currencyAmount) public view override returns (uint256) {
        return
            _getTokenAmount((currencyAmount * PURCHASE_PRICE_SCALING_FACTOR) / getLongSaleTokenPrice(block.timestamp));
    }

    function startNewRoundSale(
        uint256 openingTime,
        uint256 closingTime,
        uint256 rate,
        uint256 cap
    ) external whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        _preValidateNewSaleRound();

        // call inner function for each extension
        newSaleRound(rate);
        newSaleRoundTime(openingTime, closingTime);
        setTotalCap(cap);
    }

    function _preValidateNewSaleRound() internal view {
        require(hasClosed() || totalCapReached(), 'MintedIncreasingInterestTGE: Previous round not closed');
        require(timeInterval > 0, 'MintedIncreasingInterestTGE: Time interval increasing interest is 0');
    }
}
","keccak256":"0x1ff7f3acb19d18a12d0d75478ad51a5d5212ddad30ac4066cc0027ee5b54e948","license":"MIT"},"contracts/protocol/note-sale/base/LongSaleInterest.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '../../../libraries/UntangledMath.sol';

contract LongSaleInterest {
    uint256 public constant YEAR_LENGTH_IN_DAYS = 365;
    // All time units in seconds
    uint256 public constant MINUTE_LENGTH_IN_SECONDS = 60;
    uint256 public constant HOUR_LENGTH_IN_SECONDS = MINUTE_LENGTH_IN_SECONDS * 60;
    uint256 public constant DAY_LENGTH_IN_SECONDS = HOUR_LENGTH_IN_SECONDS * 24;
    uint256 public constant YEAR_LENGTH_IN_SECONDS = DAY_LENGTH_IN_SECONDS * YEAR_LENGTH_IN_DAYS;

    // To convert an encoded interest rate into its equivalent in percents,
    // divide it by INTEREST_RATE_SCALING_FACTOR_PERCENT -- e.g.
    //     10,000 => 1% interest rate
    uint256 public constant INTEREST_RATE_SCALING_FACTOR_PERCENT = 10**4;
    uint256 public constant PURCHASE_PRICE_SCALING_FACTOR = 10**4;

    function getPurchasePrice(
        uint256 _interestRate,
        uint256 _yield,
        uint256 _durationLengthInSec,
        uint256 _termLengthInSeconds
    ) public pure returns (uint256) {
        uint256 moreDecimal = UntangledMath.ONE / INTEREST_RATE_SCALING_FACTOR_PERCENT / 100;
        _interestRate = _interestRate * moreDecimal;
        _yield = _yield * moreDecimal;

        uint256 pricipalWithInterestInPercent = UntangledMath.ONE + _interestRate / YEAR_LENGTH_IN_SECONDS;

        uint256 pricipalWithYieldInPercent = UntangledMath.ONE + _yield / YEAR_LENGTH_IN_SECONDS;

        uint256 durationToEndTerm = _termLengthInSeconds - _durationLengthInSec;
        return
            (UntangledMath.rpow(pricipalWithInterestInPercent, _termLengthInSeconds, UntangledMath.ONE) *
                PURCHASE_PRICE_SCALING_FACTOR) /
            UntangledMath.rpow(pricipalWithYieldInPercent, durationToEndTerm, UntangledMath.ONE);
    }
}
","keccak256":"0x93d3727b740caf524dbefb6cb4adf7d947ed042075efd21161bb54945d2279f1","license":"MIT"},"contracts/protocol/note-sale/crowdsale/Crowdsale.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '../../../base/UntangledBase.sol';
import '../../../interfaces/ISecuritizationPool.sol';
import '../../../libraries/ConfigHelper.sol';
import '../../../interfaces/INoteToken.sol';
import '@openzeppelin/contracts/token/ERC20/ERC20.sol';

contract Crowdsale is UntangledBase {
    using ConfigHelper for Registry;

    Registry public registry;

    // decimal calculating for rate
    uint256 public constant RATE_SCALING_FACTOR = 10**4;

    address public pool;

    // The token being sold
    address public token;
    // The token for pay
    address public currency;

    // How many token units a buyer gets per currency.
    uint256 public rate; // support by RATE_SCALING_FACTOR decimal numbers
    bool public hasStarted;

    // Amount of currency raised
    uint256 public currencyRaised;
    uint256 public tokenRaised;

    bool internal initialized;

    uint256 public totalCap;

    mapping(address => uint256) public currencyRaisedByInvestor;

    event TokensPurchased(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);

    function __Crowdsale__init() internal onlyInitializing {
        __UntangledBase__init_unchained(_msgSender());
    }

    modifier securitizationPoolRestricted() {
        require(_msgSender() == pool, 'Crowdsale: Caller must be pool');
        _;
    }

    modifier smpRestricted() {
        require(_msgSender() == address(registry.getSecuritizationManager()), 'Crowdsale: Caller must be pool');
        _;
    }

    function addFunding(uint256 additionalCap) public whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        require(additionalCap > 0, 'Crowdsale: total cap is 0');

        totalCap = additionalCap + totalCap;
    }

    function newSaleRound(uint256 newRate) internal {
        require(!hasStarted, 'Crowdsale: Sale round overflow');

        hasStarted = true;
        rate = newRate;
    }

    function buyTokens(
        address payee,
        address beneficiary,
        uint256 currencyAmount
    ) external whenNotPaused nonReentrant smpRestricted returns (uint256) {
        uint256 tokenAmount = isLongSale() ? getLongSaleTokenAmount(currencyAmount) : _getTokenAmount(currencyAmount);

        _preValidatePurchase(beneficiary, currencyAmount, tokenAmount);

        // update state
        currencyRaised += currencyAmount;
        currencyRaisedByInvestor[beneficiary] += currencyAmount;

        tokenRaised += tokenAmount;

        _claimPayment(payee, currencyAmount);
        _processPurchase(beneficiary, tokenAmount);
        emit TokensPurchased(_msgSender(), beneficiary, currencyAmount, tokenAmount);

        _forwardFunds(ISecuritizationPool(pool).pot(), currencyAmount);

        return tokenAmount;
    }

    function isDistributedFully() public view returns (bool) {
        return currencyRaised == totalCap;
    }

    function getTokenRemainAmount() public view returns (uint256) {
        return IERC20(token).balanceOf(address(this));
    }

    function getCurrencyRemainAmount() public view virtual returns (uint256) {
        return totalCap - currencyRaised;
    }

    function isLongSale() public view virtual returns (bool) {
        return false;
    }

    function getTimeStartEarningInterest() public view returns (uint256) {
        return block.timestamp;
    }

    function getLongSaleTokenAmount(uint256 currencyAmount) public view virtual returns (uint256) {
        return rate;
    }

    function _defaultPreValidatePurchase(
        address beneficiary,
        uint256 currencyAmount,
        uint256 tokenAmount
    ) internal view {
        require(beneficiary != address(0), 'Crowdsale: beneficiary is zero address');
        //        require(currencyAmount != 0, "currency amount is 0");
        require(tokenAmount != 0, 'Crowdsale: token amount is 0');
        require(isUnderTotalCap(currencyAmount), 'Crowdsale: cap exceeded');
    }

    function _preValidatePurchase(
        address beneficiary,
        uint256 currencyAmount,
        uint256 tokenAmount
    ) internal view virtual {
        _defaultPreValidatePurchase(beneficiary, currencyAmount, tokenAmount);
    }

    function _deliverTokens(address beneficiary, uint256 tokenAmount) internal {
        INoteToken(token).mint(beneficiary, tokenAmount);
    }

    function _ejectTokens(uint256 tokenAmount) internal {
        INoteToken(token).burn(tokenAmount);
    }

    function _processPurchase(address beneficiary, uint256 tokenAmount) internal {
        _deliverTokens(beneficiary, tokenAmount);
    }

    function _claimPayment(address payee, uint256 currencyAmount) internal {
        IERC20(currency).transferFrom(payee, address(this), currencyAmount);
    }

    function _getTokenAmount(uint256 currencyAmount) public view returns (uint256) {
        require(rate > 0, 'Crowdsale: rate is 0');
        uint256 TEN = 10;
        return
            (currencyAmount * rate * TEN**ERC20(token).decimals()) /
            (RATE_SCALING_FACTOR * TEN**ERC20(currency).decimals());
    }

    function _getCurrencyAmount(uint256 tokenAmount) internal view returns (uint256) {
        if (rate == 0) return 0;
        uint256 TEN = 10;
        return
            (tokenAmount * RATE_SCALING_FACTOR * TEN**ERC20(currency).decimals()) /
            (rate * TEN**ERC20(token).decimals());
    }

    function _forwardFunds(address beneficiary, uint256 currencyAmount) internal {
        IERC20(currency).transfer(beneficiary, currencyAmount);
    }

    function setTotalCap(uint256 cap) internal {
        require(cap > 0, 'Crowdsale: cap is 0');
        require(cap >= tokenRaised, 'Crowdsale: cap is bellow token raised');

        totalCap = cap;
    }

    function totalCapReached() public view returns (bool) {
        return tokenRaised >= totalCap;
    }

    function isUnderTotalCap(uint256 currencyAmount) public view returns (bool) {
        return currencyRaised + currencyAmount <= totalCap;
    }
}
","keccak256":"0xdec83ca10a06db17e56ccf722e2e2cf7430e8155cd937560a56e580911f76954","license":"MIT"},"contracts/protocol/note-sale/crowdsale/FinalizableCrowdsale.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './TimedCrowdsale.sol';

contract FinalizableCrowdsale is TimedCrowdsale {
    bool public finalized;

    event CrowdsaleFinalized();

    function finalize(bool claimRemainToken, address remainTokenRecipient)
        public
        whenNotPaused
        nonReentrant
        onlyRole(OWNER_ROLE)
    {
        require(!finalized, 'FinalizableCrowdsale: already finalized');
        require(hasClosed() || totalCapReached(), 'FinalizableCrowdsale: not closed');

        if (!isDistributedFully() && !isLongSale()) {
            uint256 tokenRemain = 0;
            tokenRemain = _getTokenAmount(getCurrencyRemainAmount());

            if (claimRemainToken) {
                _processPurchase(remainTokenRecipient, tokenRemain);
            } else {
                _ejectTokens(tokenRemain);
            }
        }

        finalized = true;

        _finalization();
        emit CrowdsaleFinalized();
    }

    function _finalization() internal virtual {
        // solhint-disable-previous-line no-empty-blocks
    }
}
