pragma solidity ^0.8.0;

import './FinalizableCrowdsale.sol';

contract IncreasingInterestCrowdsale is FinalizableCrowdsale {
    uint32 public initialInterest;
    uint32 public finalInterest;
    uint32 public timeInterval;
    uint32 public amountChangeEachInterval;

    uint32 public pickedInterest;

    function setInterestRange(
        uint32 _initialInterest,
        uint32 _finalInterest,
        uint32 _timeInterval,
        uint32 _amountChangeEachInterval
    ) public whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        require(hasStarted, 'IncreasingInterestCrowdsale: sale already started');
        require(
            _initialInterest <= _finalInterest,
            'IncreasingInterestCrowdsale: initial interest is not less than final interest'
        );
        require(_timeInterval > 0, 'IncreasingInterestCrowdsale: time interval is 0');

        initialInterest = _initialInterest;
        finalInterest = _finalInterest;
        timeInterval = _timeInterval;
        amountChangeEachInterval = _amountChangeEachInterval;
    }

    function getCurrentInterest() public view returns (uint32) {
        if (block.timestamp < openingTime) {
            return 0;
        }

        // solhint-disable-next-line not-rely-on-time
        uint256 elapsedTime = block.timestamp - openingTime;
        uint256 numberInterval = elapsedTime / timeInterval;

        uint32 currentInterest = uint32(amountChangeEachInterval * numberInterval + initialInterest);

        if (currentInterest > finalInterest) {
            return finalInterest;
        } else {
            return currentInterest;
        }
    }

    function _finalization() internal override {
        super._finalization();

        pickedInterest = getCurrentInterest();
        ISecuritizationPool(pool).setInterestRateForSOT(pickedInterest);
    }

    function _preValidatePurchase(
        address beneficiary,
        uint256 currencyAmount,
        uint256 tokenAmount
    ) internal view override {
        super._preValidatePurchase(beneficiary, currencyAmount, tokenAmount);
        require(timeInterval > 0, 'IncreasingInterestCrowdsale: time interval not set');
    }
}
","keccak256":"0x7f69cd6309860a14bd8ec1d4b058ba2deb1b600f6fc134b25ea3e85d4c5c5e11","license":"MIT"},"contracts/protocol/note-sale/crowdsale/TimedCrowdsale.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './Crowdsale.sol';

contract TimedCrowdsale is Crowdsale {
    uint256 public openingTime;
    uint256 public closingTime;

    bool public isEnableTimeLimit;

    event TimedCrowdsaleExtended(uint256 prevClosingTime, uint256 newClosingTime);

    function __TimedCrowdsale__init() internal onlyInitializing {
        __Crowdsale__init();

        isEnableTimeLimit = true;
    }

    modifier onlyWhileOpen() {
        require(isOpen() || isLongSale(), 'TimedCrowdsale: not open');
        _;
    }

    function isOpen() public view returns (bool) {
        if (!isEnableTimeLimit) {
            return true;
        }
        // solhint-disable-next-line not-rely-on-time
        return block.timestamp >= openingTime && block.timestamp <= closingTime;
    }

    function hasClosed() public view returns (bool) {
        // solhint-disable-next-line not-rely-on-time
        return block.timestamp > closingTime;
    }

    function extendTime(uint256 newClosingTime) external whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        require(newClosingTime > closingTime, 'TimedCrowdsale: new closing time is before current closing time');

        emit TimedCrowdsaleExtended(closingTime, newClosingTime);
        closingTime = newClosingTime;
    }

    function newSaleRoundTime(uint256 newOpeningTime, uint256 newClosingTime) public whenNotPaused nonReentrant {
        require(newClosingTime >= newOpeningTime, 'TimedCrowdsale: opening time is not before closing time');
        // not accept opening time in the past
        if (newOpeningTime < block.timestamp) {
            newOpeningTime = block.timestamp;
        }

        if (newClosingTime <= newOpeningTime) {
            newClosingTime = newOpeningTime + 1;
        }

        openingTime = newOpeningTime;
        closingTime = newClosingTime;
    }

    function setUsingTimeLimit(bool usingTimeLimit) public whenNotPaused nonReentrant onlyRole(OWNER_ROLE) {
        isEnableTimeLimit = usingTimeLimit;
    }
}
","keccak256":"0xfa1f8121e150884f1060aaef28801a6e31f0c5e76fbe2e3785d586b381eafd43","license":"MIT"},"contracts/protocol/pool/DistributionTranche.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './base/SecuritizationPoolServiceBase.sol';

import '@openzeppelin/contracts/interfaces/IERC20.sol';

contract DistributionTranche is SecuritizationPoolServiceBase, IDistributionTranche {
    using ConfigHelper for Registry;

    modifier onlyOperator() {
        require(_msgSender() == address(registry.getDistributionOperator()), 'DistributionTranche: Only Operator');
        _;
    }

    function redeem(
        address usr,
        address pool,
        address notesToken,
        uint256 currencyAmount,
        uint256 tokenAmount
    ) external whenNotPaused nonReentrant onlyOperator {
        if (tokenAmount > 0) {
            require(IERC20(notesToken).transfer(pool, tokenAmount), 'DistributionTranche: token-transfer-failed');
        }
        ISecuritizationPool(pool).redeem(usr, notesToken, currencyAmount, tokenAmount);
    }

    function redeemToken(
        address noteToken,
        address usr,
        uint256 tokenAmount
    ) external whenNotPaused nonReentrant onlyOperator returns (bool) {
        return IERC20(noteToken).transferFrom(usr, address(this), tokenAmount);
    }
}
","keccak256":"0x7ea9eb9c27fb7f2d5820bb3bb848485a82d0799e8e73280dd5ffb23882f5a0b3","license":"MIT"},"contracts/protocol/pool/base/SecuritizationPoolServiceBase.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '../../../base/UntangledBase.sol';
import '../../../interfaces/ISecuritizationPool.sol';
import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '../../../libraries/ConfigHelper.sol';

contract SecuritizationPoolServiceBase is UntangledBase {
    Registry public registry;

    function initialize(Registry _registry) public initializer {
        __UntangledBase__init(_msgSender());

        registry = _registry;
    }

    function convertTokenValueToCurrencyAmount(
        address pool,
        address tokenAddress,
        uint256 tokenValue
    ) internal view returns (uint256) {
        uint256 currencyDecimals = ERC20(ISecuritizationPool(pool).underlyingCurrency()).decimals();
        uint256 tokenDecimals = ERC20(tokenAddress).decimals();

        return
            currencyDecimals > tokenDecimals
                ? tokenValue * (10**currencyDecimals - tokenDecimals)
                : tokenValue / (10**tokenDecimals - currencyDecimals);
    }

    function convertCurrencyAmountToTokenValue(
        address pool,
        address tokenAddress,
        uint256 currencyAmount
    ) internal view returns (uint256) {
        uint256 currencyDecimals = ERC20(ISecuritizationPool(pool).underlyingCurrency()).decimals();
        uint256 tokenDecimals = ERC20(tokenAddress).decimals();

        return
            currencyDecimals > tokenDecimals
                ? currencyAmount / (10**currencyDecimals - tokenDecimals)
                : currencyAmount * (10**tokenDecimals - currencyDecimals);
    }
}
